package day23;

public class StringWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name=" Ajith ";
		int n=name.length();
		System.out.println(name);
		System.out.println("Before String length "+n);
		name=name.trim();
		System.out.println(name +" "+name.length());
		
	}

}
